import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './Header.css';

const Header = () => {
    const [menuOpen, setMenuOpen] = useState(false);

    const toggleMenu = () => {
        setMenuOpen(!menuOpen);
    };

    return (
        <header className="header">
            <Link to="/" className="logo">Budget Manager</Link>
            <div className="menu-toggle" onClick={toggleMenu}>
                <span></span>
                <span></span>
                <span></span>
            </div>
            <nav className={menuOpen ? 'active' : ''}>
                <ul>
                    <li><Link to="/" onClick={toggleMenu}>Home</Link></li>
                    <li><Link to="/personal" onClick={toggleMenu}>Personal Finance</Link></li>
                    <li><Link to="/business" onClick={toggleMenu}>Business Finance</Link></li>
                    <li><Link to="/login" onClick={toggleMenu}>Login</Link></li>
                    <li><Link to="/register" onClick={toggleMenu}>Register</Link></li>
                </ul>
            </nav>
        </header>
    );
};

export default Header;
